import 'package:flutter/material.dart';
import 'package:get/get.dart';
import '../../../../../controller/vendor_home_page_flower_controller.dart';
import '../../../../../models/flower_list_view_model.dart';
import 'dart:convert';
import 'delete_alert_dialog.dart';
import 'flower_chip_list.dart';

class FlowerItem extends GetView<VendorHomePageFlowerController> {
  final FlowerListViewModel flowerItem;

  const FlowerItem({
    required this.flowerItem,
    super.key,
  });

  @override
  Widget build(BuildContext context) => Padding(
        padding: const EdgeInsets.all(8.0),
        child: GestureDetector(
            onTap: () {
              controller.goToEditFlowerPage( flowerItem: flowerItem);
            },
            child: DecoratedBox(
              decoration: BoxDecoration(
                borderRadius: BorderRadius.circular(16.0),
                boxShadow: const [
                  BoxShadow(
                    color: Colors.grey,
                    offset: Offset(0, 6),
                    blurRadius: 6.0,
                    spreadRadius: 0.0,
                  )
                ],
                image: DecorationImage(
                  image: flowerItem.image == ''
                      ? MemoryImage(base64Decode(
                          ''))
                      : MemoryImage(base64Decode(flowerItem.image)),
                  // Replace `bytes` with your image bytes
                  fit: BoxFit.cover,
                ),
              ),
              child: Container(
                height: 300,
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(16.0),
                  gradient: LinearGradient(
                    colors: [
                      Colors.transparent,
                      Colors.black.withOpacity(0.8),
                    ],
                    begin: Alignment.topCenter,
                    end: Alignment.bottomCenter,
                    stops: const [0.2, 1.0],
                  ),
                ),
                child: Padding(
                  padding: const EdgeInsets.all(16.0),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    mainAxisAlignment: MainAxisAlignment.end,
                    children: [
                      Row(
                        children: [
                          const Text(
                            'Name: ',
                            style: TextStyle(
                              color: Colors.white,
                              fontSize: 20.0,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                          Text(
                            flowerItem.name,
                            style: const TextStyle(
                              color: Colors.white,
                              fontSize: 24.0,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                        ],
                      ),
                      const SizedBox(height: 8.0),
                      Row(
                        children: [
                          const Text('Description: ',
                              style:
                                  TextStyle(color: Colors.white, fontSize: 16)),
                          Text(
                            flowerItem.shortDescription,
                            style: const TextStyle(
                              color: Colors.white,
                              fontSize: 16.0,
                            ),
                          ),
                        ],
                      ),
                      const SizedBox(height: 8.0),
                      Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: [
                          Row(
                            children: [
                              const Text('Color: ',
                                  style: TextStyle(
                                      color: Colors.white, fontSize: 16)),
                              Container(
                                decoration: BoxDecoration(
                                  color: Color(flowerItem.color),
                                  borderRadius: BorderRadius.circular(8.0),
                                ),
                                padding: const EdgeInsets.symmetric(
                                    horizontal: 16.0, vertical: 8.0),
                              ),
                            ],
                          ),
                          Row(
                            children: [
                              DeleteAlertDialog(flowerItem: flowerItem),
                            ],
                          ),
                        ],
                      ),
                      const SizedBox(height: 16.0),
                      Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: [
                          Row(children: [
                            const Text(
                              'Price: ',
                              style: TextStyle(
                                color: Colors.white,
                                fontSize: 16.0,
                                fontWeight: FontWeight.bold,
                              ),
                            ),
                            Text(
                              ('\$${flowerItem.price}'),
                              style: const TextStyle(
                                color: Colors.white,
                                fontSize: 24.0,
                                fontWeight: FontWeight.bold,
                              ),
                            ),
                          ]),
                          Row(
                            children: [
                              IconButton(
                                onPressed: () {
                                  controller.editCountFlowerMinus( flowerItem: flowerItem,);
                                },
                                icon: const Icon(
                                  Icons.remove,
                                  color: Colors.white,
                                ),
                              ),
                              const Text(
                                'Count: ',
                                style: TextStyle(
                                  color: Colors.white,
                                  fontSize: 16.0,
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                              Text(
                                flowerItem.countInStock.toString(),
                                style: const TextStyle(
                                  color: Colors.white,
                                  fontSize: 24.0,
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                              IconButton(
                                onPressed: () {
                                  controller.editCountFlowerPlus(flowerItem: flowerItem,);
                                },
                                icon: const Icon(
                                  Icons.add,
                                  color: Colors.white,
                                ),
                              ),
                            ],
                          ),
                        ],
                      ),
                      Row(
                        children: [
                          FlowerChipList(flowerItem: flowerItem),
                        ],
                      ),
                    ],
                  ),
                ),
              ),
            )),
      );
}
